.logement_owner {
  display: grid;
  justify-items: right;
}
.logement_owner .owner {
  display: flex;
  flex-direction: row;
  flex-wrap: nowrap;
  gap: 20px;
  justify-items: right;
  text-align: right;
}
.logement_owner .owner h3 {
  width: 150px;
  text-align: right;
}
.logement_owner .owner img {
  width: 64px;
  height: 64px;
  background: #C4C4C4;
  border-radius: 50px;
  object-fit: cover;
