.home {
  display: flex;
  flex-direction: column;
  width: 80%;
  margin: auto;
}

.slider {
  position: relative;
  height: 20rem;
  margin-bottom: 2rem;
  display: flex;
  align-items: center;
}
.slider img {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 1rem;
  box-shadow: black;
  opacity: 0;
  transition: opacity 0.5s ease-in-out;
}
.slider img.show {
  opacity: 1;
}
.slider .btn_container {
  display: inline-flex;
  flex-direction: row;
  width: 100%;
  justify-content: space-between;
  z-index: 1;
}
.slider .btn_container.chevron, .slider .btn_container .previous, .slider .btn_container .next {
  background-color: transparent;
  color: white;
  border: none;
  width: 100px;
  font-size: 60px;
  font-weight: 500;
}
.slider .btn_container .counter {
  position: absolute;
  bottom: 0px;
  font-size: 18px;
  margin: 10px 45%;
  width: 10%;
  color: white;
  font-weight: 600;
}
.container_chezvous {
  margin-bottom: 40px;
  height: 223px;
  border-radius: 25px;
  background-blend-mode: darken;
  background-repeat: no-repeat;
  background-size: 100%;
}
.container_chezvous h1 {
  color: #FFF;
  text-align: center;
}

.galery {
  margin-bottom: 30px;
  width: 100%;
  background-color: #f7f7f7;
  display: grid;
  gap: 20px;
  grid-template-columns: repeat(auto-fill, minmax(400px, 1fr));
  padding: 20px;
}
.galery .card_logement {
  display: flex;
  width: 404px;
  align-items: flex-end;
  position: relative;
  border-radius: 10px;
  transition: transform 200ms ease-in-out;
}
.galery .card_logement:hover {
  transform: scale(1.05);
}
