.slider {
  position: relative;
  height: 20rem;
  margin-bottom: 2rem;
  display: flex;
  align-items: center;
}
.slider img {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 1rem;
  box-shadow: black;
  opacity: 0;
  transition: opacity 0.5s ease-in-out;
}
.slider img.show {
  opacity: 1;
}
.slider .btn_container {
  display: inline-flex;
  flex-direction: row;
  width: 100%;
  justify-content: space-between;
  z-index: 1;
}
.slider .btn_container.chevron, .slider .btn_container .previous, .slider .btn_container .next {
  background-color: transparent;
  color: white;
  border: none;
  width: 100px;
  font-size: 60px;
  font-weight: 500;
}
.slider .btn_container .counter {
  position: absolute;
  bottom: 0px;
  font-size: 18px;
  margin: 10px 45%;
  width: 10%;
  color: white;
  font-weight: 600;
}

