.galery {
  margin: 30px auto;
  display: grid;
  gap: 20px;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
}
@media screen and (min-width: 992px) {
  .galery {
    background-color: #f6f6f6;
    padding: 50px;
    border-radius: 25px;
    grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
  }
}
.galery .card_logement {
  display: flex;
  position: relative;
  border-radius: 10px;
  transition: transform 200ms ease-in-out;
}
.galery .card_logement:hover {
  transform: scale(1.05);
}
.galery .card_logement img {
  display: block;
  width: 100%;
  min-height: 340px;
  object-fit: cover;
  border-radius: 10px;
}
.galery .card_logement .logement_title {
  padding: 10px;
  width: 100%;
  font-size: 18px;
  font-weight: 500;
  color: #f1f1f1;
}
.galery .card_logement .background_title {
  width: 100%;
  position: absolute;
  bottom: 0;
  border-radius: 10px;
  background: linear-gradient(180deg, rgba(255, 255, 255, 0) 0%, rgba(0, 0, 0, 0.5) 100%);
