commits.content:
@import '.variables.scss';

@mixin dropdown{
.paragraph_title{
    background-color: $primary-color;
    color: white;
    border-radius: 10px;
    padding: 13px 20px;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    position: relative;
}
.paragraph{
    background: #F7F7F7;
    color: $primary-color;
    border-radius: 10px;
    padding: 20px;
    position: relative;
    top: -20px;
    z-index: -1;
    animation: dropdown 1s ease-in;
    &.hidden{
      animation: reverse;
  }

}
  span {
    font-size: 50px;
    font-weight: 700;
    line-height: 0.1;
    position: absolute; 
    bottom: 40px;
    right: 10PX;
    cursor: pointer;
    &.open{
      transform: rotate(180deg);
      display: inline-block;
      bottom: 0;
    }
  }
}
@keyframes dropdown{
  0%{
   height: 0%;
    overflow: hidden;
  }
  50%{
 height: 50%;
  }
 100%{
   height: 100%;
  }
}
